SELECT IDPQV.DATA_ID, ${_FUNC_MODINT}(${_FUNC_MAX}(DISTINCT IDPQV.AUTH_PRIORITY_PERMISSION), 100) AS DATA_PERMISSION FROM ( SELECT IDQV.DP_AUTH_DATA_ID AS DATA_ID, ( CASE
/*创建用户最高权限*/ WHEN IDQV.DP_AUTH_DATA_CREATOR_ID IS NOT NULL AND IDQV.DP_AUTH_DATA_CREATOR_ID = '${DP_CURRENT_USER.id}' THEN (9000 + ${DP_MAX_PERMISSION})
/*这里不需加权，因为它级别最低，且有可能是负值*/ WHEN PQV.AUTH_RESOURCE_TYPE IS NULL THEN ${DP_UNSET_PERMISSION} ELSE PQV.AUTH_PRIORITY_PERMISSION END ) AS AUTH_PRIORITY_PERMISSION FROM (

		) IDQV
	LEFT JOIN
		(

		) PQV
	ON
		(PQV.AUTH_RESOURCE_TYPE = '${DP_RESOURCE_TYPE}' AND PQV.AUTH_RESOURCE = IDQV.DP_AUTH_DATA_ID)
	) IDPQV
GROUP BY
	IDPQV.DATA_ID
